import  cart from '../../images/icon-cart.svg'
import prev from '../../images/icon-previous.svg'
import next from '../../images/icon-next.svg'
import prod1 from '../../images/image-product-1.jpg'

function Productpage() {
  const [itemCount, setItemCount] = React.useState(1);
  
  return (
    <main className='w-full'>
      <div>
        <img src={prod1} alt="" />
      </div>
      <div className='p-6'>
        <div>
          <h2 className='text-orange-400 text-xs font-bold'>SNEAKER COMPANY</h2>

          <h3 className='font-bold text-3xl mt-4'>Fall Limited Edition Sneakers</h3>

          <p className='text-gray-500 mt-4'>
            These low-profile sneakers are your perfect casual wear companion. Featuring a 
            durable rubber outer sole, theyâ€™ll withstand everything the weather can offer.
          </p>
          <div className='letters flex w-full  items-center mt-4'>
            <h3 className='  w-1/2  text-xl font-bold justify-between flex items-center'>$125.00 <span className='bg-orange-100 text-orange-500 rounded text-sm pl-2 pr-2'>50%</span> </h3>
            <h2 className='w-1/2 justify-end text-right items-end line-through text-gray-400 font-semibold'>$250.00</h2>
          </div>

          
        </div>

        <div className='pieces rounded-xl flex w-auto justify-between items-center mt-5 mb-4 pl-5 pr-5 h-12'>
          <button><img src={minus} alt="minus" /></button>
          <h2 className='font-bold'>0</h2>
          <button><img src={plus} alt="plus" /></button>
        </div>
        <div className='flex w-auto justify-between  h-12'>
          <button className='bg-orange-500 rounded-xl text-white flex w-full items-center justify-center'>
            <img src={cart} className='ml-2 mr-2 h-4 w-4' alt="cart-img" /> <h3>Add to cart</h3> 
          </button>
        </div>
      </div>
    </main>
    
    
  );
}
