import React from 'react'
import next from '../../images/icon-next.svg'
import prev from '../../images/icon-previous.svg'
import close from '../../images/icon-close.svg'

function Preview({clickedImage, handleRotationRight, 
        setClickedImage,
        setModal2,
        modal2,
        toggleModal2,
        data, 
        currentIndex}) {
    const handleClick= (e)=>{
        if (e.target.classList.contains('dismiss')){
            setClickedImage(null);

        }
 
    }  
    return (
    <div className=' modal fixed left-0 top-0 right-0 bottom-0 bg-gray-500/75 flex items-center justify-center'>
        <div className='modal-content flex flex-col bg-white items-center justify-center'>
            <div className='modal-header p=4'>
                <h4 className=' modal-title m-0'> Modal Title</h4>
            </div>
            <div >
                <button className='pr-16 ' onClick={toggleModal2}> <img src={close} alt="" /> </button>
            </div>
             
            <div className=' flex flex row'>
                <button>
                    <img src={prev} alt="" />
                </button>
                <div className='modal-body p-4 left-0 flex flex-row items-center justify-center'>
                    {data.data.map((item, index)=>(
                    <div key={index} >
                        <img src={item.thumb} data-index={index} alt='' onClick= {()=> handleClick(item,index)} className={`${currentIndex=== index ?'h-24 w-24 rounded-2xl cursor-pointer hover:opacity-50 border-2 border-orange-500 opacity-50 ': 'h-24 w-24 rounded-2xl cursor-pointer hover:opacity-50 '}`} />
                    </div>
                    ))}
                </div>
                <button>
                    <img src={next} alt="" />
                </button>
            </div>
            
             
            
        </div>
    </div>
  )
}

export default Preview




