import React from 'react'
import next from '../../images/icon-next.svg'
import prev from '../../images/icon-previous.svg'
import close from '../../images/icon-close.svg'

function Preview({clickedImage, handleRotationRight, setClickedImage}) {
    const handleClick= (e)=>{
        if (e.target.classList.contains('dismiss')){
            setClickedImage(null);

        }
 
    }  
    return (
    <div className=' modal fixed left-0 top-0 right-0 bottom-0 bg-black flex items-center justify-center'>
        <div className='modal-content '>
            <div className='modal-header '>
                <h4 className=' modal-title'> Modal Title</h4>
            </div>
            <button>
                <img src={prev} alt="" />
            </button> 
            <div className='modal-body'>
                <h2 className='font-bold pt-2 '> Modal Content</h2>
            </div>
            <button>
                <img src={next} alt="" />
            </button> 
            <div className='modal-footer '>
                <button className='modal-content '> <img src={close} alt="" /> </button>
            </div>
        </div>
    </div>
  )
}

export default Preview




