import React from 'react'
import next from '../../images/icon-next.svg'
import prev from '../../images/icon-previous.svg'

function Preview({clickedImage, handleRotationRight, setClickedImage}) {
    const handleClick= (e)=>{
        if (e.target.classList.contains('dismiss')){
            setClickedImage(null);

        }
 
    }  
    return (
    <div>
        <div className='overlay dismiss'>
            <img src={clickedImage} alt="bigger Picture" />
            <span className='dismiss' onClick= {handleClick}>x</span>

            <div className="" onClick={handleRotationRight}>
                <img src={next} alt="" />
            </div>
            
        </div>
        </div>
  )
}

export default Preview




