import React, {useState} from 'react'
import next from '../../images/icon-next.svg'
import prev from '../../images/icon-previous.svg'
import close from '../../images/icon-close.svg'

function Preview({clickedImage, handleRotationRight, 
        setClickedImage,
        setCurrentIndex,
        setModal2,
        modal2,
        toggleModal2,
        data, 
        currentIndex}) {

            const [currentIndex2, setCurrentIndex2] = useState(null);
            const [clickedImage2, setClickedImage2] = useState(null);
            const handleClick = (item, index) => {
                setCurrentIndex2(index);
                setClickedImage2(item.main);
                console.log(index)
              }
    return (
    <div className='modal2 fixed top-0 left-0 right-0 bottom-0 bg-black/75 flex flex-col items-center content-center justify-center '>
            <div className='right-0' >
                <button className='' onClick={toggleModal2}> <img src={close} alt="" /> </button>
            </div>
            <div className=' flex flex-col items-center content-center '>
                
                <div className='flex flex-row items-center justify-center'>
                    <button>
                    <img src={prev} alt="" />
                    </button>
                    <div className='  flex '>
                        <img src={currentIndex2=== null ? data.data[0].main : clickedImage2 } onClick='' alt="" className='lg:rounded-2xl w-96 h-96 cursor-pointer' />
                    </div>
                    <button>
                        <img src={next} alt="" />
                    </button>
                </div> 
                    <div className='flex justify-between pt-6 w-80  '>
                        {data.data.map((item, index)=>(
                        <div key={index} >
                            <img src={item.thumb} data-index={index} alt='' onClick= {()=> handleClick(item,index)} 
                            className={`${currentIndex2=== index ?'h-16 w-16 rounded-2xl cursor-pointer hover:opacity-50 border-2 border-orange-500 opacity-50 ': 'h-16 w-16 rounded-2xl cursor-pointer hover:opacity-50 '}`} />
                        </div>
                        ))}
                </div>
        </div>
    </div>
  )
}

export default Preview




